import React, { useState, useEffect, useRef } from 'react';
import { toast } from 'react-toastify';
import axios from 'axios';
import { API_URL } from '../../config';

const PaymentMethods = ({ onPaymentSuccess, onPaymentError }) => {
  const [isProcessing, setIsProcessing] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('gpay');

  // References to payment form elements
  const cardFormRef = useRef(null);
  const gpayFormRef = useRef(null);
  const phonepeFormRef = useRef(null);

  // Effect to handle payment method toggle
  useEffect(() => {
    const handlePaymentMethodChange = () => {
      if (cardFormRef.current && gpayFormRef.current && phonepeFormRef.current) {
        // Hide all forms first
        cardFormRef.current.style.display = 'none';
        gpayFormRef.current.style.display = 'none';
        phonepeFormRef.current.style.display = 'none';

        // Show the selected form
        if (paymentMethod === 'card') {
          cardFormRef.current.style.display = 'block';
        } else if (paymentMethod === 'gpay') {
          gpayFormRef.current.style.display = 'block';
        } else if (paymentMethod === 'phonepe') {
          phonepeFormRef.current.style.display = 'block';
        }
      }
    };

    handlePaymentMethodChange();

    // Set up event listeners for radio buttons
    const setupRadioListeners = () => {
      const cardRadio = document.getElementById('payment-card');
      const gpayRadio = document.getElementById('payment-gpay');
      const phonepeRadio = document.getElementById('payment-phonepe');

      if (cardRadio && gpayRadio && phonepeRadio) {
        cardRadio.addEventListener('change', () => setPaymentMethod('card'));
        gpayRadio.addEventListener('change', () => setPaymentMethod('gpay'));
        phonepeRadio.addEventListener('change', () => setPaymentMethod('phonepe'));

        return () => {
          cardRadio.removeEventListener('change', () => setPaymentMethod('card'));
          gpayRadio.removeEventListener('change', () => setPaymentMethod('gpay'));
          phonepeRadio.removeEventListener('change', () => setPaymentMethod('phonepe'));
        };
      }
    };

    const cleanup = setupRadioListeners();
    return cleanup;
  }, [paymentMethod]);

  const processPayment = async () => {
    setIsProcessing(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication token not found');
      }

      // Validate payment information based on selected method
      if (paymentMethod === 'card') {
        // Validate card details
        const cardNumber = document.querySelector('#card-payment-form input[placeholder="1234 5678 9012 3456"]').value;
        const expDate = document.querySelector('#card-payment-form input[placeholder="MM/YY"]').value;
        const cvc = document.querySelector('#card-payment-form input[placeholder="123"]').value;

        if (!cardNumber || !expDate || !cvc) {
          toast.error('Please fill in all card details');
          setIsProcessing(false);
          return;
        }
      } else if (paymentMethod === 'gpay' || paymentMethod === 'phonepe') {
        // Validate UPI reference ID
        const upiRefId = document.querySelector(`#${paymentMethod}-payment-form input[placeholder="UPI Reference ID"]`).value;

        if (!upiRefId) {
          toast.error('Please enter the UPI reference ID');
          setIsProcessing(false);
          return;
        }
      }

      // In a real app, this would process the payment with a payment gateway
      // For demo purposes, we'll just simulate a successful payment
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Show payment method specific success message
      let paymentMethodName = 'card';
      if (paymentMethod === 'gpay') paymentMethodName = 'Google Pay';
      if (paymentMethod === 'phonepe') paymentMethodName = 'PhonePe';

      // Update user subscription tier
      const response = await axios.put(`${API_URL}/users/subscription`,
        {
          subscription_tier: 'premium',
          payment_method: paymentMethod
        },
        { headers: { Authorization: `Bearer ${token}` }}
      );

      toast.success(`Payment successful via ${paymentMethodName}! Subscription upgraded to Premium.`);

      // Call the success callback
      if (onPaymentSuccess) {
        onPaymentSuccess(response.data);
      }
    } catch (error) {
      console.error('Payment processing error:', error);
      toast.error('Failed to process payment. Please try again.');

      // Call the error callback
      if (onPaymentError) {
        onPaymentError(error);
      }
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div>
      <div className="mb-4">
        <div className="flex items-center space-x-3 mb-3">
          <div className="flex items-center">
            <input
              id="payment-card"
              name="payment-method"
              type="radio"
              className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300"
            />
            <label htmlFor="payment-card" className="ml-2 block text-sm text-gray-700">
              Credit/Debit Card
            </label>
          </div>
          <div className="flex items-center">
            <input
              id="payment-gpay"
              name="payment-method"
              type="radio"
              defaultChecked
              className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300"
            />
            <label htmlFor="payment-gpay" className="ml-2 block text-sm text-gray-700">
              Google Pay
            </label>
          </div>
          <div className="flex items-center">
            <input
              id="payment-phonepe"
              name="payment-method"
              type="radio"
              className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300"
            />
            <label htmlFor="payment-phonepe" className="ml-2 block text-sm text-gray-700">
              PhonePe
            </label>
          </div>
        </div>

        {/* Credit Card Form */}
        <div id="card-payment-form" className="space-y-3" ref={cardFormRef}>
          <div>
            <label className="block text-xs font-medium text-gray-500 mb-1">Card Number</label>
            <input
              type="text"
              placeholder="1234 5678 9012 3456"
              className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
          <div className="grid grid-cols-2 gap-3">
            <div>
              <label className="block text-xs font-medium text-gray-500 mb-1">Expiration Date</label>
              <input
                type="text"
                placeholder="MM/YY"
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              />
            </div>
            <div>
              <label className="block text-xs font-medium text-gray-500 mb-1">CVC</label>
              <input
                type="text"
                placeholder="123"
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              />
            </div>
          </div>
        </div>

        {/* Google Pay Form */}
        <div id="gpay-payment-form" className="hidden space-y-3" ref={gpayFormRef}>
          <div className="bg-gray-50 p-3 rounded-md">
            <div className="flex items-center mb-2">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 48 48" className="h-6 w-6 mr-2">
                <path fill="#EA4335" d="M24 9.5c3.54 0 6.71 1.22 9.21 3.6l6.85-6.85C35.9 2.38 30.47 0 24 0 14.62 0 6.51 5.38 2.56 13.22l7.98 6.19C12.43 13.72 17.74 9.5 24 9.5z"/>
                <path fill="#4285F4" d="M46.98 24.55c0-1.57-.15-3.09-.38-4.55H24v9.02h12.94c-.58 2.96-2.26 5.48-4.78 7.18l7.73 6c4.51-4.18 7.09-10.36 7.09-17.65z"/>
                <path fill="#FBBC05" d="M10.53 28.59c-.48-1.45-.76-2.99-.76-4.59s.27-3.14.76-4.59l-7.98-6.19C.92 16.46 0 20.12 0 24c0 3.88.92 7.54 2.56 10.78l7.97-6.19z"/>
                <path fill="#34A853" d="M24 48c6.48 0 11.93-2.13 15.89-5.81l-7.73-6c-2.15 1.45-4.92 2.3-8.16 2.3-6.26 0-11.57-4.22-13.47-9.91l-7.98 6.19C6.51 42.62 14.62 48 24 48z"/>
                <path fill="none" d="M0 0h48v48H0z"/>
              </svg>
              <span className="font-medium text-gray-700">Pay with Google Pay</span>
            </div>
            <p className="text-sm text-gray-600 mb-3">Send payment to the following Google Pay account:</p>
            <div className="bg-white p-3 rounded border border-gray-300 flex items-center justify-between">
              <div>
                <p className="font-medium text-gray-800">+917871691614</p>
                <p className="text-xs text-gray-500">WhatsApp News Bot Premium</p>
              </div>
              <button
                type="button"
                className="text-indigo-600 hover:text-indigo-800 text-sm font-medium"
                onClick={() => {
                  navigator.clipboard.writeText('+917871691614');
                  toast.success('GPay number copied to clipboard!');
                }}
              >
                Copy
              </button>
            </div>
            <div className="mt-3 flex items-center">
              <div className="flex-shrink-0 h-10 w-10">
                <img className="h-10 w-10" src="https://upload.wikimedia.org/wikipedia/commons/thumb/c/c7/Google_Pay_Logo_%282020%29.svg/1200px-Google_Pay_Logo_%282020%29.svg.png" alt="Google Pay" />
              </div>
              <div className="ml-4">
                <h3 className="text-sm font-medium text-gray-900">Scan QR Code</h3>
                <p className="text-xs text-gray-500">Open Google Pay app and scan the QR code</p>
              </div>
            </div>
            <p className="mt-2 text-xs text-gray-500">After payment, enter the UPI reference ID below:</p>
            <input
              type="text"
              placeholder="UPI Reference ID"
              className="mt-1 w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
        </div>

        {/* PhonePe Form */}
        <div id="phonepe-payment-form" className="hidden space-y-3" ref={phonepeFormRef}>
          <div className="bg-gray-50 p-3 rounded-md">
            <div className="flex items-center mb-2">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 48 48" className="h-6 w-6 mr-2">
                <rect width="48" height="48" fill="#5f259f"/>
                <path fill="#fff" d="M24.0002,11.5001 C29.5002,11.5001 34.0002,16.0001 34.0002,21.5001 C34.0002,27.0001 29.5002,31.5001 24.0002,31.5001 C18.5002,31.5001 14.0002,27.0001 14.0002,21.5001 C14.0002,16.0001 18.5002,11.5001 24.0002,11.5001 Z M24.0002,15.5001 C20.6002,15.5001 18.0002,18.1001 18.0002,21.5001 C18.0002,24.9001 20.6002,27.5001 24.0002,27.5001 C27.4002,27.5001 30.0002,24.9001 30.0002,21.5001 C30.0002,18.1001 27.4002,15.5001 24.0002,15.5001 Z"/>
                <path fill="#fff" d="M24.0002,33.5001 C16.3002,33.5001 10.0002,27.2001 10.0002,19.5001 L10.0002,10.5001 L38.0002,10.5001 L38.0002,19.5001 C38.0002,27.2001 31.7002,33.5001 24.0002,33.5001 Z M14.0002,14.5001 L14.0002,19.5001 C14.0002,25.0001 18.5002,29.5001 24.0002,29.5001 C29.5002,29.5001 34.0002,25.0001 34.0002,19.5001 L34.0002,14.5001 L14.0002,14.5001 Z"/>
                <rect width="4" height="8" x="22" y="34" fill="#fff"/>
                <rect width="12" height="4" x="18" y="38" fill="#fff"/>
              </svg>
              <span className="font-medium text-gray-700">Pay with PhonePe</span>
            </div>
            <p className="text-sm text-gray-600 mb-3">Send payment to the following PhonePe account:</p>
            <div className="bg-white p-3 rounded border border-gray-300 flex items-center justify-between">
              <div>
                <p className="font-medium text-gray-800">+917871691614</p>
                <p className="text-xs text-gray-500">WhatsApp News Bot Premium</p>
              </div>
              <button
                type="button"
                className="text-indigo-600 hover:text-indigo-800 text-sm font-medium"
                onClick={() => {
                  navigator.clipboard.writeText('+917871691614');
                  toast.success('PhonePe number copied to clipboard!');
                }}
              >
                Copy
              </button>
            </div>
            <div className="mt-3 flex items-center">
              <div className="flex-shrink-0 h-10 w-10">
                <img className="h-10 w-10" src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/5f/PhonePe_Logo.png/800px-PhonePe_Logo.png" alt="PhonePe" />
              </div>
              <div className="ml-4">
                <h3 className="text-sm font-medium text-gray-900">Scan QR Code</h3>
                <p className="text-xs text-gray-500">Open PhonePe app and scan the QR code</p>
              </div>
            </div>
            <p className="mt-2 text-xs text-gray-500">After payment, enter the UPI reference ID below:</p>
            <input
              type="text"
              placeholder="UPI Reference ID"
              className="mt-1 w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
        </div>
      </div>

      <div className="mt-4 flex items-center">
        <input
          id="save-payment"
          name="save-payment"
          type="checkbox"
          className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
        />
        <label htmlFor="save-payment" className="ml-2 block text-sm text-gray-900">
          Save payment method for future payments
        </label>
      </div>

      <button
        onClick={processPayment}
        disabled={isProcessing}
        className="w-full flex justify-center py-3 px-4 mt-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all duration-200"
      >
        {isProcessing ? (
          <span className="flex items-center">
            <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            Processing...
          </span>
        ) : (
          <span className="flex items-center">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
            </svg>
            Pay Now - ₹1/month
          </span>
        )}
      </button>
      <p className="mt-2 text-xs text-gray-500 text-center">
        Your payment will be processed immediately. You can cancel anytime.
      </p>
    </div>
  );
};

export default PaymentMethods;
